import React, { useState } from "react";

function Filter(props) {
	const [data, setData] = useState("");

	const onTrigger = (event) => {
		setData(event.target.value);
		console.warn(data);
	};
	let Series = Series.filter((Serie) => Serie.title.indexOf(data) !== -1);

	return (
		<div className="nav-bar">
			<header>
				<h1>123-Serie </h1>
				<div className="input-info">
					<p>Search </p>
					<input
						type="text"
						placeholder="Enter Serie Title"
						value={data}
						onChange={onTrigger.bind(this)}
					></input>
				</div>
			</header>
			<hr />
		</div>
	);
}

export default Filter;
